import React, { useState, useEffect } from "react";
import axios from "axios";
import MathJax from "react-mathjax";
import "./Quiz.css";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';


const App = () => {
  const [question, setQuestion] = useState({});
  const [answer, setAnswer] = useState({});
  const [hint , getHint] = useState({});
  const [solution , getSolution] = useState({});
  const [showPopup, setShowPopup] = useState(false);

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const topicIds = [
    "AreaUnderTheCurve_1",
    "AreaUnderTheCurve_2",
    "AreaUnderTheCurve_3",
    "AreaUnderTheCurve_4",
    "AreaUnderTheCurve_5",
  ];
  const fetchData = async (id) => {
    await axios
      .get(
        `https://0h8nti4f08.execute-api.ap-northeast-1.amazonaws.com/getQuestionDetails/getquestiondetails?QuestionID=${id}`
      )
      .then((response) => {
        console.log(response.data[0].Question);
        setQuestion((prevQuestions) => ({
          ...prevQuestions,
          [id]: response.data[0].Question,
        }));
        setAnswer((prevAnswers) => ({
          ...prevAnswers,
          [id]: response.data[0].Result2,
        }));
      })
      .catch((error) => console.error(error));
  };

  useEffect(() => {
    for (const id of topicIds) {
      fetchData(id);
    }
  }, []);

  const handleNextQuestion = () => {
    if (currentQuestion < topicIds.length - 1) {
      setCurrentQuestion((prevQuestion) => prevQuestion + 1);
    }
  };

  const handlePreviousQuestion = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion((prevQuestion) => prevQuestion - 1);
    }
  };

  const handleButtonClick = () => {
    const confirmed = window.confirm("Do you want to see the popup card?");

    if (confirmed) {
      setShowPopup(true);
    }
  };

  return (
    <MathJax.Provider>

      <div className="container">

        <div className="help-button">
          <Button onClick={handleButtonClick} className="hint">Hint?</Button>
          {showPopup && <div onClose={() => setShowPopup(false)} />}
          {/* <Button className="answer">Correct Answer</Button> */}
        </div>

        <div className="card">
          <div>
            <h3>Question :  {topicIds[currentQuestion]}</h3>
            <h4><MathJax.Node inline formula={question[topicIds[currentQuestion]]} /></h4>
            {/* <p>
              Answer :{" "}
              <MathJax.Node inline formula={answer[topicIds[currentQuestion]]} />
            </p> */}
          </div>
        </div>
        <div className="navigation-Buttons gap-2">
          <Button
            onClick={handlePreviousQuestion}
            disabled={currentQuestion === 0}
            variant="success"
            size="lg"
          >
            Previous
          </Button>
          <Button
            onClick={handleNextQuestion}
            disabled={currentQuestion === topicIds.length - 1}
            variant="success"
            size="lg"
          >
            Next
          </Button>
        </div>
      </div>
    </MathJax.Provider>
  );
};

export default App;
