import React, {useState , useEffect} from "react";
import "./Popup.css";
import axios from "axios";
import MathJax from "react-mathjax";
import Button from "react-bootstrap/Button";

const PopupCard = ({ onClose }) => {

  const [solution , setSolution] = useState({});
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const topicIds = [
    "AreaUnderTheCurve_1",
    "AreaUnderTheCurve_2",
    "AreaUnderTheCurve_3",
    "AreaUnderTheCurve_4",
    "AreaUnderTheCurve_5",
  ];
  const fetchData = async (id) => {
    await axios
      .get(
        `https://0h8nti4f08.execute-api.ap-northeast-1.amazonaws.com/getQuestionDetails/getquestiondetails?QuestionID=${id}`
      )
      .then((response) => {
        console.log(response.data[0].Question);
        setSolution((prevQuestions) => ({
          ...prevQuestions,
          [id]: response.data[0].Result2,
        }));
      })
      .catch((error) => console.error(error));
  };

  useEffect(() => {
    for (const id of topicIds) {
      fetchData(id);
    }
  }, []);


  return (
    <div className="popup-card-overlay">
      <div className="popup-card">
      <MathJax.Node inline formula={solution[topicIds[currentQuestion]]} />
        <Button onClick={onClose}>Close</Button>
      </div>
    </div>
  );
};

export default PopupCard;
