import React, { useState, useEffect } from 'react'
import './index.css'
const Quiz = () => {
  const Questionbank = [
    {
      Question: "What is your name?",
      AnswersText: [
        { Answer: "Prashant", isCorrect: true },
        { Answer: "Abhinav", isCorrect: false },
        { Answer: "Tejas", isCorrect: false },
        { Answer: "Anjana", isCorrect: false },
      ]
    },
    {
      Question: "Who is the PM of India?",
      AnswersText: [
        { Answer: "Rahul Gandhi", isCorrect: false },
        { Answer: "Amit Shah", isCorrect: false },
        { Answer: "Narendra Modi", isCorrect: true },
        { Answer: "Arvind Kejriwal", isCorrect: false },
      ]
    },
    {
      Question: "What is the national animal?",
      AnswersText: [
        { Answer: "Lion", isCorrect: false },
        { Answer: "Tiger", isCorrect: true },
        { Answer: "Panther", isCorrect: false },
        { Answer: "Elephant", isCorrect: false },
      ]
    },
    {
      Question: "React is the framework of which language?",
      AnswersText: [
        { Answer: "Javascript", isCorrect: true },
        { Answer: "C++", isCorrect: false },
        { Answer: "Python", isCorrect: false },
        { Answer: "Java", isCorrect: false },
      ]
    },
    {
      Question: "Who owne Tesla?",
      AnswersText: [
        { Answer: "Anil Ambani", isCorrect: false },
        { Answer: "Sundar Pichai", isCorrect: false },
        { Answer: "Mark Zukerburg", isCorrect: false },
        { Answer: "Elon Musk", isCorrect: true },
      ]
    },
  ]

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);

  const handleAnswerResponse=(isCorrect)=>
{
    if(isCorrect)
    {
        setScore(score+1);
    }

   const nextQuestion= currentQuestion+1;
   if(nextQuestion<Questionbank.length)
   {
    setCurrentQuestion(nextQuestion);
   }
   else{
    setShowScore(true);
   }
}

const resetQuiz=()=>
{
    setCurrentQuestion(0);
    setScore(0);
    setShowScore(false);
}

    return (
        <div className='app'>
            {showScore ? (
                <div className='score-section'>
                    You have scored {score} out of {Questionbank.length}
                    <>
                       <button type="submit" onClick={resetQuiz}>Play Again!!</button>
                    </>
                </div>
            )
                : (
                    <>
                        <div className='question-section'>
                            <div className='question-count'>
                               <span>{currentQuestion+1}</span>/{Questionbank.length}
                            </div>

                            <div className='question-text'>
                             {Questionbank[currentQuestion].Question}
                            </div>
                        </div>

                        <div className='answer-section'>
                          {Questionbank[currentQuestion].AnswersText.map((answer)=>
                          (
                              <button onClick={()=>handleAnswerResponse(answer.isCorrect)}>{answer.Answer}</button>
                          ))}
                        </div>
                    </>
                )
            }

        </div>
    );
}

export default Quiz;
