import React, { useState, useEffect } from "react";
import axios from "axios";
import MathJax from "react-mathjax";

const App = () => {
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [userAnswers, setUserAnswers] = useState({});
  const [showResults, setShowResults] = useState(false);

  useEffect(() => {
    const topicIds = ["AreaUnderTheCurve_1", "AreaUnderTheCurve_2", "AreaUnderTheCurve_3", "AreaUnderTheCurve_4", "AreaUnderTheCurve_5"];
    const fetchData = async (id) => {
      const response = await axios.get(
        `https://0h8nti4f08.execute-api.ap-northeast-1.amazonaws.com/getQuestionDetails/getquestiondetails?QuestionID=${id}`
      );
      setQuestions((prevQuestions) => [...prevQuestions, response.data[0]]);
    };

    for (const id of topicIds) {
      fetchData(id);
    }
  }, []);

  const handleAnswerChange = (event) => {
    const { name, value } = event.target;
    setUserAnswers((prevAnswers) => ({ ...prevAnswers, [name]: value }));
  };

  const handlePrevClick = () => {
    setCurrentQuestionIndex((prevIndex) => prevIndex - 1);
  };

  const handleNextClick = () => {
    setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const score = questions.reduce((totalScore, question, index) => {
      if (userAnswers[index] === questions.Result1) {
        return totalScore + 1;
      }
      return totalScore;
    }, 0);
    console.log(`Your score is ${score} out of ${questions.length}`);
    setShowResults(true);
  };

  return (
    <div>
      {showResults ? (
        <div>
          <h2>Results</h2>
          <p>
            Your score is {questions.reduce((totalScore, question, index) => {
              if (userAnswers[index] === question.Result1) {
                return totalScore + 1;
              }
              return totalScore;
            }, 0)} out of {questions.length}
          </p>
        </div>
      ) : (
        <form onSubmit={handleSubmit}>
          <MathJax.Provider>
            <div>
              <h3>{questions.QuestionID}</h3>
              <MathJax.Node inline formula={questions.Question} />
            </div>
            <div>
              <label htmlFor="answer">Your Answer:</label>
              <input
                type="text"
                name={currentQuestionIndex}
                id="answer"
                value={userAnswers[currentQuestionIndex] || ""}
                onChange={handleAnswerChange}
              />
            </div>
            <div>
              {currentQuestionIndex > 0 && (
                <button type="button" onClick={handlePrevClick}>
                  Previous
                </button>
              )}
              {currentQuestionIndex < questions.length - 1 ? (
                <button type="button" onClick={handleNextClick}>
                  Next
                </button>
              ) : (
                <button type="submit">Submit</button>
              )}
            </div>
          </MathJax.Provider>
        </form>
      )}
    </div>
  )
};

export default App;
