import React, { useState, useEffect } from "react";
import axios from "axios";
import MathJax from "react-mathjax";
import "./Quiz.css";
import Button from 'react-bootstrap/Button';
import { BsArrowRightCircle, BsArrowLeftCircle } from "react-icons/bs";

const App = () => {
  const [question, setQuestion] = useState({});
  const [hint, setHint] = useState({});
  const [result, setResult] = useState({});
  const [showHint, setShowHint] = useState(false);
  const [showResult, setShowResult] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const topicIds = ["AreaUnderTheCurve_1", "AreaUnderTheCurve_2", "AreaUnderTheCurve_3", "AreaUnderTheCurve_4", "AreaUnderTheCurve_5",];
  const fetchData = async (id) => {
    await axios
      .get(
        `https://0h8nti4f08.execute-api.ap-northeast-1.amazonaws.com/getQuestionDetails/getquestiondetails?QuestionID=${id}`
      )
      .then((response) => {
        setQuestion((prevQuestions) => ({
          ...prevQuestions,
          [id]: response.data[0].Question,
        }));
        // console.log(response.data[0].Question);
        // console.log(response.data[0].Explanation2),
        console.log(response.data[0].Result2),
        setHint((prevHints) => ({
          ...prevHints,
          [id]: response.data[0].Explanation2,
        }));
        setResult((prevResults) => ({
          ...prevResults,
          [id]: response.data[0].Result2,
        }));
      })
      .catch((error) => console.error(error));
  };

  useEffect(() => {
    for (const id of topicIds) {
      fetchData(id);
    }
  }, []);


  const handleNextQuestion = () => {
    if (currentQuestion < topicIds.length - 1) {
      setCurrentQuestion((prevQuestion) => prevQuestion + 1);
      setShowHint(false);
      setShowResult(false);
    }
  };

  const handlePreviousQuestion = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion((prevQuestion) => prevQuestion - 1);
      setShowHint(false);
      setShowResult(false);
    }
  };


  return (
    <MathJax.Provider>
      <div className="container">
        <div className="help-button">
          <Button variant="warning" className="hint" onClick={() => setShowHint(true)}>Hint?</Button>
          <Button variant="danger" className="answer" onClick={() => setShowResult(true)}>Correct Answer</Button>
        </div>
        <div className="navigation-Buttons">
          <button
            onClick={handlePreviousQuestion}
            disabled={currentQuestion === 0}
            className="prev-button">
            <BsArrowLeftCircle />
          </button>
          <div className="card">
            <div>
              <h3>Question : {topicIds[currentQuestion]}</h3>
              <h4>
                <MathJax.Node
                  inline
                  formula={question[topicIds[currentQuestion]]}
                />
              </h4>
            </div>
          </div>
          <button
            onClick={handleNextQuestion}
            disabled={currentQuestion === topicIds.length - 1}
            className="next-button">
            <BsArrowRightCircle />
          </button>
        </div>
        {showHint && (
          <div className="popup">
            <h4>Hint</h4>
            <MathJax.Node inline formula={hint[topicIds[currentQuestion]]} />
            <Button variant="secondary" onClick={() => setShowHint(false)}>
              Close
            </Button>
          </div>
        )}
        {showResult && (
          <div className="popup">
            <h4>Correct Answer</h4>
            <MathJax.Node inline formula={result[topicIds[currentQuestion]]} />
            <Button variant="secondary" onClick={() => setShowResult(false)}>
              Close
            </Button>
          </div>
        )}
      </div>
    </MathJax.Provider>
  );

};

export default App;
