import React, { useState, useEffect} from "react";
import axios from "axios";
import { MathJaxContext , MathJax } from "better-react-mathjax";
const App = () => {
  const [question, setQuestion] = useState(""); 
  const topicIds = ["AreaUnderTheCurve_1", "AreaUnderTheCurve_2", "AreaUnderTheCurve_3", "AreaUnderTheCurve_4", "AreaUnderTheCurve_5"];
  const fetchData = (id) => {
    axios.get(
      `https://0h8nti4f08.execute-api.ap-northeast-1.amazonaws.com/getQuestionDetails/getquestiondetails?QuestionID=${id}`
    )
    .then((response) => {
      console.log(response.data[0].Question)
      setQuestion(response.data[0].Question);
    })
    .catch((error) => console.error(error));
  }

  useEffect(() => {
        for (const id of topicIds) {
          fetchData(id);
        }
  }, []);

  const sol = `\text{$ =\int_{-4}^{-2}\left(x^2-4\right) d x+\int_{-2}^2\left(4-x^2\right) d x+\int_2^3\left(x^2-4\right) d x $ }`;
  return (
    <div>
      <MathJaxContext>
      {topicIds.map((id) => (
        <div key={id}>
          <h3>{id}</h3>
          <MathJax>{question}</MathJax>
        </div>
      ))}
      <MathJax>{sol}</MathJax>
      </MathJaxContext>
    </div>
  );
};

export default App;
