import React, { useState, useEffect} from "react";
import axios from "axios";
import  MathJax from "react-mathjax";
import './Quiz.css'

const App = () => {
  const [question, setQuestion] = useState({}); 
  const [answer, setAnswer] = useState({}); 
  const topicIds = ["AreaUnderTheCurve_1", "AreaUnderTheCurve_2", "AreaUnderTheCurve_3", "AreaUnderTheCurve_4", "AreaUnderTheCurve_5"];
  const fetchData = async (id) => {
   await axios.get(
      `https://0h8nti4f08.execute-api.ap-northeast-1.amazonaws.com/getQuestionDetails/getquestiondetails?QuestionID=${id}`
    )
    .then((response) => {
      console.log(response.data[0].Question)
      setQuestion((prevQuestions) => ({
        ...prevQuestions,
        [id] : response.data[0].Question
      }));
      setAnswer((prevAnswers) => ({
        ...prevAnswers,
        [id] : response.data[0].Result2
      }));
    })
    .catch((error) => console.error(error));
  }

  useEffect(() => {
        for (const id of topicIds) {
          fetchData(id);
        }
  }, []);
  return (
    <div className="conatiner">
      <div className="card">
      <MathJax.Provider>
      {topicIds.map((id) => (
        <div key={id}>
          <h3>{id}</h3>
          <MathJax.Node inline formula={question[id]} />
          <MathJax.Node inline formula={answer[id]} />
        </div>
      ))}
      </MathJax.Provider>
    </div>
    </div>
  );
};

export default App;
