import React, { useState, useEffect } from "react";
import axios from "axios";
import MathJax from "react-mathjax";
import "./Quiz.css";
import Button from 'react-bootstrap/Button';
import { BsArrowRightCircle, BsArrowLeftCircle } from "react-icons/bs";

const App = () => {
  const [question, setQuestion] = useState({});
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const topicIds = ["AreaUnderTheCurve_1", "AreaUnderTheCurve_2", "AreaUnderTheCurve_3", "AreaUnderTheCurve_4", "AreaUnderTheCurve_5",];
  const fetchData = async (id) => {
    await axios
      .get(
        `https://0h8nti4f08.execute-api.ap-northeast-1.amazonaws.com/getQuestionDetails/getquestiondetails?QuestionID=${id}`
      )
      .then((response) => {
        console.log(response.data[0].Question);
        setQuestion((prevQuestions) => ({
          ...prevQuestions,
          [id]: response.data[0].Question,
        }));
      })
      .catch((error) => console.error(error));
  };

  useEffect(() => {
    for (const id of topicIds) {
      fetchData(id);
    }
  }, []);

  const handleNextQuestion = () => {
    if (currentQuestion < topicIds.length - 1) {
      setCurrentQuestion((prevQuestion) => prevQuestion + 1);
    }
  };

  const handlePreviousQuestion = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion((prevQuestion) => prevQuestion - 1);
    }
  };


  return (
    <MathJax.Provider>
      <div className="container">
        <div className="navigation-Buttons">
          {/* <button
            onClick={handlePreviousQuestion}
            disabled={currentQuestion === 0}
            className="prev-button">
            <BsArrowLeftCircle />
          </button> */}
          <a
            href="#"
            onClick={handlePreviousQuestion}
            disabled={currentQuestion === 0}
            className="previous">
            &laquo; Previous
          </a>
          <div className="card">
            <div>
              <h3>Question : {topicIds[currentQuestion]}</h3>
              <h4>
                <MathJax.Node
                  inline
                  formula={question[topicIds[currentQuestion]]}
                />
              </h4>
            </div>
          </div>
          {/* <button
            onClick={handleNextQuestion}
            disabled={currentQuestion === topicIds.length - 1}
            className="next-button">
            <BsArrowRightCircle />
          </button> */}
          <a
            href="#"
            onClick={handleNextQuestion}
            disabled={currentQuestion === topicIds.length - 1}
            className="next">
            Next &raquo;
          </a>
        </div>

      </div>
    </MathJax.Provider>
  );

};

export default App;
