import React, { useState, useEffect } from "react";
import axios from "axios";
import MathJax from "react-mathjax";

const App = () => {
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState(new Array(5).fill(null));

  useEffect(() => {
    axios
      .get(
        "https://0h8nti4f08.execute-api.ap-northeast-1.amazonaws.com/getQuestionDetails/getquestiondetails?QuestionID=AreaUnderTheCurve_1"
      )
      .then((response) => {
        setQuestions(response.data);
      })
      .catch((error) => console.error(error));
  }, []);

  const handleAnswer = (event) => {
    event.preventDefault();
    const newAnswers = [...answers];
    newAnswers[currentQuestionIndex] = event.target.answer.value;
    setAnswers(newAnswers);
    if (currentQuestionIndex < 4) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  const handlePrevious = () => {
    setCurrentQuestionIndex(currentQuestionIndex - 1);
  };

  const handleNext = () => {
    setCurrentQuestionIndex(currentQuestionIndex + 1);
  };

  const handleRestart = () => {
    setCurrentQuestionIndex(0);
    setAnswers(new Array(5).fill(null));
  };

  const score = answers.filter((answer) => answer !== null).length;

  return (
    <div>
      {questions.length > 0 && currentQuestionIndex < 5 ? (
        <form onSubmit={handleAnswer}>
          <MathJax.Provider>
            <div key={currentQuestionIndex}>
              <h3>Question {currentQuestionIndex + 1}</h3>
              <MathJax.Node formula={questions[currentQuestionIndex].Question} />
              <br />
              <br />
              <label>
                Answer:
                <input type="text" name="answer" />
              </label>
              <br />
              <br />
              <button type="submit">Submit</button>
            </div>
          </MathJax.Provider>
        </form>
      ) : (
        <div>
          <h3>Quiz Complete!</h3>
          <p>Your score: {score}/5</p>
          <button onClick={handleRestart}>Restart</button>
        </div>
      )}
      {currentQuestionIndex > 0 && (
        <button onClick={handlePrevious}>Previous</button>
      )}
      {currentQuestionIndex < 4 && (
        <button onClick={handleNext}>Next</button>
      )}
    </div>
  );
};

export default App;
